
CREATE DATABASE IF NOT EXISTS coffeedb;

USE coffeedb;
DROP TABLE IF EXISTS reviews;

CREATE TABLE reviews(
	id INT,
	name VARCHAR(128),
	roaster VARCHAR(128),
	roast VARCHAR(32),
	dollars_per_ounce DOUBLE(5,2),
	origin VARCHAR(128),
	review VARCHAR(1024),
	roaster_link VARCHAR(128),
	flavor VARCHAR(512)
);

