CREATE DATABASE IF NOT EXISTS cosmetics_db;

USE cosmetics_db;
DROP TABLE IF EXISTS korean_skincare;

CREATE TABLE IF NOT EXISTS korean_skincare(
   product_name   VARCHAR(53) NOT NULL PRIMARY KEY
  ,product_brand  VARCHAR(22) NOT NULL
  ,price          INTEGER  NOT NULL
  ,product_review VARCHAR(1357) NOT NULL
  ,product_type   VARCHAR(21) NOT NULL
);
